path  = '/Users/ronizoller/PycharmProjects/TreeReconciliation/trees/COG2602'


G_text = open(path+'/GeneTree(binary)_local.txt', 'r').read()
S_text = open(path+'/phyliptree(binary,all).phy', 'r').read()

with open(path+'/FASTA.txt','r') as fp:
    for line in fp:
        if line[0] == '>':
            start = 1
            end = line.find(' ')
            gene = ''
            while start < end :
                gene = gene + line[start]
                start += 1
            gene = gene.replace("_"," ")
            start  = line.find('[')+1
            end  = line.find(']')
            specie = ''
            while start < end :
                if(line[start] != ' '):
                    specie = specie + line[start]
                start += 1
            if (S_text.find(specie) != -1):
                print("'"+gene+"' : '"+specie+"',\n")

